import React, { Component } from "react";

export default class Timer extends Component {
    state = {
        timer: 0,
        backward: false,
        value:''
    }
    tick = () => {
        let { timer, backward } = this.state;
        let next;
        if (backward == true) {
            next = timer - 1;
        } else {
            next = timer + 1;
        }
        if (next === 10 || next === 0) {
            backward = next === 10;
            this.setState({ timer: next, backward })
        } else {
            this.setState({ timer: next })
        }
    }
