import React, { Component } from "react";

export default class Timer extends Component {
    state = {
        timer: 0,
        backward: false
    }
    tick  =() => { 
        const { timer, backward } = this.state;
        let next;
        if (backward == true) {
            next = timer - 1;
        } else {
            next = timer + 1;
        }
        if(next==timer)
          this.setState({ timer: next })
    }
    componentDidMount() {
        this.interval = setInterval(this.tick, 1000);

