import React, { Component } from "react";

export default class Timer extends Component {
    state = {
        timer: 0,
        backward: false
    }
    constructor() {
        super();
        //this.tick = this.bind(this.tick);

    }
    tick  =() => {
        const { timer, backward } = this.state;
        let next;
        if (backward) {
            next = timer - 1;
        } else {
            next = timer + 1;
        }
        this.setState({ timer: next })
    }
    componentDidMount() {
        this.interval = setInterval(this.tick, 1000);

    }
